div.pre:
set terminal svg set output "kernels.svg"
delta(x) = x ? 1 : 0; sqr(x) = x*x; cub(x) = x*x*x;
set samples 5000 plot [u=-1.2:1.2] [0:1.2] \ 0.5*delta(abs(u)<1) title "Uniform", \ (1.0-abs(u))*delta(abs(u)<1.0) title "Triangle", \ 3.0/4.0*(1.0-sqr(u))*delta(abs(u)<1.0) title "Epanechnikov", \ 15.0/16.0*sqr(1.0-sqr(u))*delta(abs(u)<1.0) title "Quartic", \ 35.0/32.0*cub(1.0-sqr(u))*delta(abs(u)<1.0) title "Triweight", \ 1.0/sqrt(2.0*pi)*exp(-0.5*sqr(u)) title "Gaussian", \ pi/4.0*cos(pi/2.0*u)*delta(abs(u)<1.0) title "Cosine";

